import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'

export default function Homepage() {
	const myAsyncFunction = () => {
		return new Promise((resolve, reject) => { // resolve ve reject fonksiyonları şu işe yarar :
			// resolve fonksiyonu ile promise'in sonucu başarılı bir şekilde döndürülür. 
			//reject fonksiyonu ile promise'in sonucu başarısız bir şekilde döndürülür.
			setTimeout(() => {
				resolve('Hello World')
			}, 3000)
		})
		useEffect(() => {
			myAsyncFunction().then((response) => {
				console.log("işlem başarılı cevap: ", response);
			}) // async işlem başarı ise (resolve edildi ise)
				.catch((error) => {
					console.log("işlem başarısız cevap: ", error);
				}) // async işlem başarısız ise (reject edildi ise)
				.finally(() => {
					console.log("işlem bitti");
				}); // finally fonksiyonu ile promise'in sonucu ne olursa olsun çalıştırılacak fonksiyon belirlenir.
		}, []);
	}
	return (
		<div>
			<button className='btn btn-primary'>Homepage</button>
			<br />
			{
				/* spa'larda href yerine to kullanılır.*/
			}
			<Link to={"/about"}>go About</Link>
		</div>
	)
}
