import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState({});

  useEffect(() => {
    // Sayfa yüklendiğinde ürünü getir
    fetchProduct();
  }, []);

  const fetchProduct = async () => {
    try {
      const response = await axios.get(`http://dummyjson.com/products/${id}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Error fetching product:', error);
    }
  };

  return (
    <div className="container mt-5" style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <div className="card" style={{ width: '25rem' }}>
        <img src={product?.thumbnail} className="card-img-top rounded" alt="Product Thumbnail" style={{ maxWidth: '100%', maxHeight: '60%', height: 'auto' }} />
        <div className="card-body">
          <h5 className="card-title">{product.title}</h5>
          <p className="card-text">{product?.description}</p>
        </div>
        <ul className="list-group list-group-flush">
          <li className="list-group-item"><strong>Brand:</strong> {product.brand}</li>
          <li className="list-group-item"><strong>Price:</strong> {product.price}</li>
          <li className="list-group-item"><strong>Category:</strong> {product.category}</li>
          <li className="list-group-item"><strong>Rating:</strong> {product.rating}</li>
        </ul>
      </div>
    </div>
  );
}
