    let {id} = useParams()

    const [product, setProduct] = useState({})

    useEffect(() =>{
        fetchProductDetail();
    },[]);

    const fetchProductDetail = async () => {
        try {
            let response = await axios.get("https://dummyjson.com/products/"+id);
            console.log("API Response:", response.data); // API yanıtını kontrol et
