import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Sayfa yüklendiğinde ürünü getir
    fetchProduct();
  }, []);

  const fetchProduct = async () => {
    try {
      let response = await axios.get(`https://dummyjson.com/products/${id}`);
      console.log('API Response:', response.data);
      setProduct(response.data);
    } catch (error) {
      console.error('Ürün detayını getirme hatası:', error);
    }
  };



  return (
    <div className="container mt-5">
      {product && (
        <div className="row">
          {/* ... rest of the code */}
        </div>
      )}
       <ul className="list-group list-group-flush">
                        <li className="list-group-item">{product.title}</li>
                        <li className="list-group-item">{product.brand}</li>
                        <li className="list-group-item">{product.price}</li>
                        <li className="list-group-item">{product.category}</li>
                        <li className="list-group-item">{product.rating}</li>
                    </ul>
    </div>
  );
}
