import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function ProductDetails() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Sayfa yüklendiğinde ürünü getir
    fetchProduct();
  }, []);

  const fetchProduct = async () => {
    try {
      let response = await axios.get(`https://dummyjson.com/products/{id}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Ürün detayını getirme hatası:', error);
    }
  };


  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6">
          <img src={product.thumbnail} className="img-fluid" alt={product.title} />
        </div>
        <div className="col-md-6">
          <h2>{product.title}</h2>
          <p>{product.description}</p>
          <p>Price: ${product.price}</p>
          {/* İsterseniz diğer ürün detay bilgilerini ekleyebilirsiniz */}
        </div>
        <ul className="list-group list-group-flush">
          <li className="list-group-item">Brand: {product.brand}</li>
          <li className="list-group-item">Price: {product.price}</li>
          <li className="list-group-item">Category: {product.category}</li>
          <li className="list-group-item">Rating: {product.rating}</li>
        </ul>
      </div>
    </div>
  );
}
