import React, { useState } from 'react';
import './AdProduct.css';

export default function AddNewProduct() {
  const [newProduct, setNewProduct] = useState({
    title: '',
    description: '',
    price: '',
    category: '',
    image: '',
    stock: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value,
    }));
  };

  const handleAddProduct = async () => {
    try {
      const response = await fetch('https://dummyjson.com/products/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newProduct),
      });

      if (response.ok) {
        const addedProduct = await response.json();
        console.log('Yeni ürün eklendi:', addedProduct);
      } else {
        console.error('Ürün ekleme hatası:', response.statusText);
      }
    } catch (error) {
      console.error('Ürün ekleme hatası:', error.message);
    }
  };

  return (
    <div>
      <h2>Yeni Ürün Ekle</h2>
      <form>
        <div>
          <label>
            Title:
            <input type="text" name="title" value={newProduct.title} onChange={handleInputChange} />
          </label>
        </div>
        <div>
          <label>
            Description:
            <input type="text" name="description" value={newProduct.description} onChange={handleInputChange} />
          </label>
        </div>
        <div>
          <label>
            Price:
            <input type="number" name="price" value={newProduct.price} onChange={handleInputChange} />
          </label>
        </div>
        <div>
          <label>
            Category:
            <input type="text" name="category" value={newProduct.category} onChange={handleInputChange} />
          </label>
        </div>
        <div>
          <label>
            Image URL:
            <input type="text" name="image" value={newProduct.image} onChange={handleInputChange} />
          </label>
        </div>
        <div>
          <label>
            Stock:
            <input type="number" name="stock" value={newProduct.stock} onChange={handleInputChange} />
          </label>
        </div>
        <button type="button" onClick={handleAddProduct}>
          Ürünü Ekle
        </button>
      </form>
    </div>
  );
}
